import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {useNavigate} from "react-router-dom";
import InputGroup from 'react-bootstrap/InputGroup';

import {  Row, Col, Container } from "react-bootstrap";
import Form from 'react-bootstrap/Form';

export default function Login() {
     let navigate=useNavigate();
const [username, setusername] = useState("")
const [password, setpassword] = useState("")

    const LoginHandeller=()=>{
       fetch(`http://localhost:3001/auth/v1`,{
        method:"POST",
        headers:{
            "Content-type":"application/json"
        },
        body:JSON.stringify({username,password})
       })
       .then(res=>res.json())
       .then(data=>{
        console.log(data);
        localStorage.setItem("token",data.token)
        localStorage.setItem("user",username)
        
        navigate("/")
        window.location.reload(false)
       })
    }
    return (
        <Container className="container-fluid" >
    
          <Row className='d-flex justify-content-center align-items-center '>
            <Col col='12'>
    
              <Card className='bg-dark text-white my-5 mx-auto' style={{borderRadius: '1rem', maxWidth: '400px'}}>
                <Card.Body className='p-5 d-flex flex-column align-items-center mx-auto w-100'>
    
                  <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
                  <p className="text-white-50 mb-5">Please enter your login and password!</p>
                  <InputGroup hasValidation>
                  <Form.Control className="mb-4 mx-5 w-100" type="text" placeholder="Username"  onChange={(e)=>setusername(e.target.value)} size="lg" required isInvalid />
      <br />
      <Form.Control className="mb-4 mx-5 w-100" type="password" placeholder="password" onChange={(e)=>setpassword(e.target.value)} size="lg" required isInvalid />
      <Form.Control.Feedback type="invalid">
        Please choose a username.
      </Form.Control.Feedback>
    </InputGroup>
                  <p className="small mb-3 pb-lg-2"><a className="text-white-50" href="#!">Forgot password?</a></p>
                  <Button outline className='mx-2 px-5' color='white' size='lg' onClick={LoginHandeller} >
                    Login
                  </Button>
    
    
                  <div>
                    <p className="mb-0">Don't have an account? <a href="/register" className="text-white-50 fw-bold">Sign Up</a></p>
    
                  </div>
                </Card.Body>
              </Card>
    
            </Col>
          </Row>
    
        </Container>
      );
}
