import React, { useEffect, useState } from 'react'
import {  Row, Col, Container } from "react-bootstrap";

import Card from '../card/Card'

function Dashboard({query, setQuery}) {

  const [NewsData, setNewsData] = useState([]);
  var url ='';
    useEffect(() => {

        if(query !== ''){
       
          url = 'https://newsapi.org/v2/everything?q='+ query +'&apiKey=435fbf24d4a14e7c90a56c7077571511'
      } 
      else{
         url= 'https://newsapi.org/v2/top-headlines?country=in&apikey=435fbf24d4a14e7c90a56c7077571511'
      };
    
  
     fetch(url)
        .then((response) => response.json())
        .then((NewsData) => {
          setNewsData(NewsData.articles);
        });        
    }
    , [])

  return (
    <Container className="container-fluid" >
     <Row >
        {NewsData.map((news,index) => (
            <Col key={index} xs={15} md={4} lg={3}>

              <Card news={news}  />
              </Col>
        ))}
      </Row>
    </Container>

  );
}

export default Dashboard
