import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {useNavigate} from "react-router-dom";
import UserService from '../../services/user.service';
import {User} from '../../models/user';

import {  Row, Col, Container } from "react-bootstrap";
import Form from 'react-bootstrap/Form';

export default class Login extends React.Component{
  constructor(props) {
    super(props);

    if(UserService.currentUserValue) {
      this.props.history.push('/');
    }

    this.state = {
      user: new User('',''),
      submitted: false,
      loading: false,
      errorMessage: ''
    };
  }

  handleChange(e) {
    var {name, value} = e.target;
    var user = this.state.user;
    user[name] = value;
    this.setState({user: user});
  }

  handleLogin(e) {
    e.preventDefault();

    this.setState({submitted: true});
    const {user} = this.state;

    if(!(user.username && user.password)){
      return;
    }

    this.setState({loading: true});
    UserService.login(user).then(data => {
      this.props.history.push("/home");
    }, error => {
      this.setState({
        errorMessage: "Username or password is not valid",
        loading: false
      });
    });
  }

  render() {
    const {user, submitted, loading, errorMessage} = this.state;
    return (
        <Container className="container-fluid" >
    
          <Row className='d-flex justify-content-center align-items-center '>
            <Col col='12'>
    
              <Card className='bg-dark text-white my-5 mx-auto' style={{borderRadius: '1rem', maxWidth: '400px'}}>
                <Card.Body className='p-5 d-flex flex-column align-items-center mx-auto w-100'>
    
                  <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
                  <p className="text-white-50 mb-5">Please enter your login and password!</p>
    
                  <Form.Control className="mb-4 mx-5 w-100" type="text" placeholder="Username" value={user.username} onChange={(e)=>this.handleChange(e)} size="lg" />
      <br />
      {submitted && !user.username &&
                <div className="help-block">Username is required</div>
              }
      <Form.Control className="mb-4 mx-5 w-100" type="password" placeholder="password" value={user.password} onChange={(e)=>this.handleChange(e)} size="lg" />
      {submitted && !user.password &&
                <div className="help-block">Password is required</div>
              }
                  <p className="small mb-3 pb-lg-2"><a className="text-white-50" href="#!">Forgot password?</a></p>
                  <Button outline className='mx-2 px-5' onClick={(e) => this.handleLogin(e)} disabled={loading} color='white' size='lg' >
                    Login
                  </Button>
    
    
                  <div>
                    <p className="mb-0">Don't have an account? <a href="/register" className="text-white-50 fw-bold">Sign Up</a></p>
    
                  </div>
                </Card.Body>
              </Card>
    
            </Col>
          </Row>
    
        </Container>
      );
  }
}
