import React from 'react';
import Button from 'react-bootstrap/Button';
import card from 'react-bootstrap/Card';
import {Transaction} from '../../models/transaction'
import userService from '../../services/user.service'
function Card({news}) {
 // const fs = require('fs');
 const currentUser = JSON.parse(localStorage.getItem("currentUser"))
 let API_URL = 'http://localhost:8090/news/';
  const saveNews =  () => {
  fetch(API_URL +'tag',{
    method:"POST",
    headers:{
      "Content-type":"application/json"
    },
    body:JSON.stringify(currentUser.id,news)
  })

}


  const enroll = () => {

    const transaction = new Transaction(currentUser.id, news);
    userService.createTransaction(transaction).then(data => {
      this.setState({ infoMessage: 'You enrolled the course successfully.' });
    }, error => {
      this.setState({ errorMessage: 'Unexpected error occurred.' });
    });
  }


  return  (
      <card  className="card my-3" style={{ width: '18rem'  }}>
        <card.Img variant="top" src={news.urlToImage} />
        <card.Body>
          <card.Title>{news.title}</card.Title>
          <Button variant="primary" >Read Now</Button>
          
          <Button className="pull-right" variant="secondary" onClick={() => saveNews(news)}>Favouriates</Button>
        </card.Body>
      </card>
    );
  
}

export default Card
