import { useRef, useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

import {  Row, Col, Container } from "react-bootstrap";
import Form from 'react-bootstrap/Form';
import {User} from '../../models/user';
import { Link, useNavigate, useLocation } from 'react-router-dom';
export default function Login() {

 
  const navigate = useNavigate();
  const location = useLocation();
  const from = location.state?.from?.pathname || "/";
  const API_URL = 'http://localhost:8090/user/';
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState('');
  const [pwd, setPwd] = useState('');
  const [errMsg, setErrMsg] = useState('');
  const [validated, setValidated] = useState(false);
  useEffect(() => {
      userRef.current.focus();
  }, [])

  useEffect(() => {
      setErrMsg('');
  }, [user, pwd])

const handleChange=(e) =>{
  var { name, value } = e.target;
  var user = this.state.user;
  user[name] = value;
  this.setState({user: user});
}
 

   const LoginHandeller=(user,pwd)=>{
      fetch(API_URL + 'login',{
       method:"GET",
       headers:{
           "Content-type":"application/json"
       },
       body:JSON.stringify({user,pwd})
      })
      .then(res=>res.json())
      .then(data=>{
       console.log(data);
       localStorage.setItem("token",data.token)
       localStorage.setItem("user",user)
       
       navigate("/")
       window.location.reload(false)
      }, error => {
    
        setErrMsg('Username or password is not valid');
        errRef.current.focus();
    
    });
   }

    return (
      <Container className="container-fluid" >
    <Form noValidate validated={validated} onSubmit={handleSubmit}>
      <Row className='d-flex justify-content-center align-items-center '>
        <Col col='12'>

          <Card className='bg-dark text-white my-5 mx-auto' style={{borderRadius: '1rem', maxWidth: '400px'}}>
            <Card.Body className='p-5 d-flex flex-column align-items-center mx-auto w-100'>

              <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
              <p className="text-white-50 mb-5">Please enter your login and password!</p>
              <Form.Control.Feedback type="invalid" ref={errRef}>
              Please choose a username.
            </Form.Control.Feedback>

              <Form.Control className="mb-4 mx-5 w-100" type="text" placeholder="Username" ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setUser(e.target.value)}
                    value={user}
                    required
                    size="lg" />
  <br />
  <Form.Control className="mb-4 mx-5 w-100" type="password" placeholder="password" onChange={(e) => setPwd(e.target.value)}
                    value={pwd}
                    required
                    size="lg" />
              <p className="small mb-3 pb-lg-2"><a className="text-white-50" href="#!">Forgot password?</a></p>
              <Button outline className='mx-2 px-5' color='white' size='lg' Button type="submit">
                Login
              </Button>


              <div>
                <p className="mb-0">Don't have an account? <a href="/register" className="text-white-50 fw-bold">Sign Up</a></p>

              </div>
            </Card.Body>
          </Card>

        </Col>
      </Row>
      </Form>
    </Container>
  );

}
