import React from 'react';
import Button from 'react-bootstrap/Button';
import card from 'react-bootstrap/Card';
import Transaction from '../../models/transaction'

function Card({news}) {
 // const fs = require('fs');
  const saveNews =  () => {
  fetch("http://localhost:3001/saved-articles",{
    method:"POST",
    headers:{
      "Content-type":"application/json"
    },
    body:JSON.stringify(news)
  })

}


const enroll=(news)=> {

  var transaction = new Transaction(this.state.currentUser.id, news);
  CourseService.createTransaction(transaction).then(data => {
    this.setState({infoMessage: 'You enrolled the course successfully.'});
  }, error => {
    this.setState({errorMessage: 'Unexpected error occurred.'});
  });
}


  return  (
      <card  className="card my-3" style={{ width: '18rem'  }}>
        <card.Img variant="top" src={news.urlToImage} />
        <card.Body>
          <card.Title>{news.title}</card.Title>
          <Button variant="primary" >Read Now</Button>
          <Button variant="secondary" onClick={() => saveNews()}>Favouriates</Button>
        </card.Body>
      </card>
    );
  
}

export default Card
