import React, { useEffect, useState } from 'react'
import {  Row, Col, Container } from "react-bootstrap";
import UserService from '../../services/user.service';
import Card from '../card/Card'
import { useParams } from 'react-router-dom';
import queryString from 'query-string';
import { useLocation } from 'react-router-dom';

function Dashboard() {

  const [NewsData, setNewsData] = useState([]);
  
  
const location = useLocation();
const parsed = localStorage.getItem("query") 

    useEffect(() => {

     
       
        if(parsed !== null){
       
         url = 'https://newsapi.org/v2/everything?&apiKey=435fbf24d4a14e7c90a56c7077571511&q='+parsed
      } 
      else{
        url= 'https://newsapi.org/v2/top-headlines?country=in&apikey=435fbf24d4a14e7c90a56c7077571511'
      };
    
  
     fetch(url)
        .then((response) => response.json())
        .then((NewsData) => {
          setNewsData(NewsData.articles);
        });
    
    }
    , [])

  return (
    <Container className="container-fluid" >
     <Row >
        {NewsData.map((news,index) => (
            <Col key={index} xs={15} md={4} lg={3}>

              <Card news={news}  />
              </Col>
        ))}
      </Row>
    </Container>

  );
}

export default Dashboard
