import React from 'react';
import Button from 'react-bootstrap/Button';
import card from 'react-bootstrap/Card';
import {Transaction} from '../../models/transaction'
import userService from '../../services/user.service'
function Card({news}) {
 // const fs = require('fs');
 const currentUser = JSON.parse(localStorage.getItem("currentUser"))
 let API_URL = 'http://localhost:8090/news/';
  const saveNews =  () => {
    fetch(API_URL +'tag',{
    method:"POST",
    headers:{
      'Content-Type':'application/json; charset=UTF-8'
    },
    
    body:JSON.stringify(new Transaction(currentUser.id, news))
  })

}




  return  (
      <card  className="card my-3" style={{ width: '18rem'  }}>
        <card.Img variant="top" src={news.urlToImage} />
        <card.Body>
          <card.Title>{news.title}</card.Title>
          <Button variant="primary" >Read Now</Button>
          
          <Button className="pull-right" variant="secondary" onClick={() => saveNews()}>Favouriates</Button>
        </card.Body>
      </card>
    );
  
}

export default Card
