import React, { useState, useContext } from 'react'

import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import Navbar from 'react-bootstrap/Navbar';
import {useNavigate} from "react-router-dom"
import Button from 'react-bootstrap/Button';
import qs from 'query-string';
import { createSearchParams } from 'react-router-dom';

function Header() {

  const currentUser = JSON.parse(localStorage.getItem("currentUser"))



  var [q, setq] = useState('');
  const onChange=(event)=>{
     window.location.href = '/dashboard?'+ q;  
    window.location.reload(false)
    localStorage.setItem("query",q) 
  }

  return (
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
      <Container className="container-fluid">
        <Navbar.Brand href="/">News Portal</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="me-auto">
      
          <Nav.Link variant="outline-success" href="/dashboard">Dashboard</Nav.Link >
          <Nav.Link href="/readnow"> Favouriates</Nav.Link>
      
        </Nav>
       
        <Nav>
          <Navbar.Brand href="#">{ localStorage.getItem("currentUser")  ? "Welcome back "+currentUser.firstname:""}</Navbar.Brand>
        </Nav>
           
       
            {localStorage.getItem("currentUser") ? (
              <Nav>
                <Form className="d-flex">
                  <Form.Control
                    type="search"
                    placeholder="Search"
                    className="me-2"
                    aria-label="Search"
                    value={q}
                    onChange={(e)=>setq(e.target.value)}
                   
                  />
                  <Button variant="outline-success"  onClick={onChange}>Search</Button>
                </Form>
              <Nav.Link href="/logout"> Logout</Nav.Link>
              </Nav>
             
                  ) : (
                <Nav>
                <Nav.Link href="/login">Login</Nav.Link>
                <Nav.Link href="/register"> Register</Nav.Link>
                </Nav>
                  )}
          

        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Header;
